floatTest.mo.content:
import Debug "mo:base/Debug";
import Float "mo:base/Float";

Debug.print("Float");

do {
  Debug.print("  abs");

  assert(Float.abs(1.1) == 1.1);
  assert(Float.abs(-1.1) == 1.1);
};

do {
  Debug.print("  ceil");

  assert(Float.ceil(1.1) == 2.0);
};

do {
  Debug.print("  floor");

  assert(Float.floor(1.1) == 1.0);
};

do {
  Debug.print("  trunc");

  assert(Float.trunc(1.0012345789) == 1.0);
};

do {
  Debug.print("  nearest");

  assert(Float.nearest(1.00001) == 1.0);
  assert(Float.nearest(1.99999) == 2.0);
};

do {
  Debug.print("  min");

  assert(Float.min(1.1, 2.2) == 1.1);
};

do {
  Debug.print("  max");

  assert(Float.max(1.1, 2.2) == 2.2);
};

do {
  Debug.print("  sin");

  assert(Float.sin(0.0) == 0.0);
};

do {
  Debug.print("  cos");

  assert(Float.cos(0.0) == 1.0);
};

do {
  Debug.print("  toFloat64");

  assert(Float.toInt64(1e10) == (10000000000 : Int64));
  assert(Float.toInt64(-1e10) == (-10000000000 : Int64));
};

do {
  Debug.print("  ofFloat64");

  assert(Float.fromInt64(10000000000) == 1e10);
  assert(Float.fromInt64(-10000000000) == -1e10);
};


do {
  Debug.print("  format");

  assert(Float.format(#exact, 20.12345678901) == "20.12345678901");
  assert(Float.format(#fix 6, 20.12345678901) == "20.123457");
  assert(Float.format(#exp 9, 20.12345678901) == "2.012345679e+01");
  assert(Float.format(#gen 12, 20.12345678901) == "20.123456789");
  assert(Float.format(#hex 10, 20.12345678901) == "0x1.41f9add374p+4");
};


do {
  Debug.print("  Pi: " # Float.toText(Float.pi));
  Debug.print("  arccos(-1.0): " # Float.toText(Float.arccos(-1.)));

  assert(Float.pi == Float.arccos(-1.));
};

do {
  Debug.print("  e: " # debug_show(Float.toText(Float.e)));
  Debug.print("  exp(1): " # debug_show(Float.toText(Float.exp(1))));

  assert(Float.e == Float.exp(1));
};

resultTest.mo.content:
import Result "mo:base/Result";
import Int "mo:base/Int";
import Array "mo:base/Array";
import List "mo:base/List";

import Suite "mo:matchers/Suite";
import M "mo:matchers/Matchers";
import T "mo:matchers/Testable";

func makeNatural(x : Int) : Result.Result<Nat, Text> =
  if (x >= 0) { #ok(Int.abs(x)) } else { #err(Int.toText(x) # " is not a natural number.") };

func largerThan10(x : Nat) : Result.Result<Nat, Text> =
  if (x > 10) { #ok(x) } else { #err(Int.toText(x) # " is not larger than 10.") };

let chain = Suite.suite("chain", [
  Suite.test("ok -> ok",
    Result.chain<Nat, Nat, Text>(makeNatural(11), largerThan10),
    M.equals(T.result<Nat,Text>(T.natTestable, T.textTestable, #ok(11)))
  ),
  Suite.test("ok -> err",
    Result.chain<Nat, Nat, Text>(makeNatural(5), largerThan10),
    M.equals(T.result<Nat,Text>(T.natTestable, T.textTestable, #err("5 is not larger than 10.")))
  ),
  Suite.test("err",
    Result.chain<Nat, Nat, Text>(makeNatural(-5), largerThan10),
    M.equals(T.result<Nat,Text>(T.natTestable, T.textTestable, #err("-5 is not a natural number.")))
  ),
]);

let flatten = Suite.suite("flatten", [
  Suite.test("ok -> ok",
    Result.flatten<Nat, Text>(#ok(#ok(10))),
    M.equals(T.result<Nat,Text>(T.natTestable, T.textTestable, #ok(10)))
  ),
  Suite.test("err",
    Result.flatten<Nat, Text>(#err("wrong")),
    M.equals(T.result<Nat,Text>(T.natTestable, T.textTestable, #err("wrong")))
  ),
  Suite.test("ok -> err",
    Result.flatten<Nat, Text>(#ok(#err("wrong"))),
    M.equals(T.result<Nat,Text>(T.natTestable, T.textTestable, #err("wrong")))
  ),
]);

let iterate = Suite.suite("iterate", do {
  var tests : [Suite.Suite] = [];
  var counter : Nat = 0;
  Result.iterate(makeNatural(5), func (x : Nat) { counter += x });
  tests := Array.append(tests, [Suite.test("ok", counter, M.equals(T.nat(5)))]);
  Result.iterate(makeNatural(-10), func (x : Nat) { counter += x });
  tests := Array.append(tests, [Suite.test("err", counter, M.equals(T.nat(5)))]);
  tests
});

let suite = Suite.suite("Result", [
  chain,
  flatten,
  iterate,
]);

Suite.run(suite);

arrayTest.mo.content:
import Array "mo:base/Array";
import Int "mo:base/Int";
import Nat "../src/Nat";
import Text "mo:base/Text";
import Result "mo:base/Result";
import Suite "mo:matchers/Suite";
import T "mo:matchers/Testable";
import M "mo:matchers/Matchers";

let suite = Suite.suite("Array", [
  Suite.test(
    "init",
    Array.freeze(Array.init<Int>(3, 4)),
    M.equals(T.array<Int>(T.intTestable, [4, 4, 4]))),
  Suite.test(
    "init empty",
    Array.freeze(Array.init<Int>(0, 4)),
    M.equals(T.array<Int>(T.intTestable, []))),
  Suite.test(
    "tabulate",
    Array.tabulate<Int>(3, func (i : Nat)= i * 2),
    M.equals(T.array<Int>(T.intTestable, [0, 2, 4]))),
  Suite.test(
    "tabulate empty",
    Array.tabulate<Int>(0, func (i : Nat) = i),
    M.equals(T.array<Int>(T.intTestable, []))),
  Suite.test(
    "tabulateVar",
    Array.freeze(Array.tabulateVar<Int>(3, func (i : Nat)= i * 2)),
    M.equals(T.array<Int>(T.intTestable, [0, 2, 4]))),
  Suite.test(
    "tabulateVar empty",
    Array.freeze(Array.tabulateVar<Int>(0, func (i : Nat) = i)),
    M.equals(T.array<Int>(T.intTestable, []))),
  Suite.test(
    "freeze",
    Array.freeze<Int>([ var 1, 2, 3 ]),
    M.equals(T.array<Int>(T.intTestable, [ 1, 2, 3 ]))
  ),
  Suite.test(
    "freeze empty",
    Array.freeze<Int>([var]),
    M.equals(T.array<Int>(T.intTestable, []))
  ),
  Suite.test(
    "thaw round trip",
    Array.freeze(Array.thaw<Int>([1, 2, 3])),
    M.equals(T.array<Int>(T.intTestable, [1, 2, 3]))
  ),
  Suite.test(
    "thaw round trip empty",
    Array.freeze(Array.thaw<Int>([])),
    M.equals(T.array<Int>(T.intTestable, []))
  ),
  Suite.test(
    "equal",
    Array.equal<Int>([1, 2, 3], [1, 2, 3], Int.equal),
    M.equals(T.bool(true))
  ),
  Suite.test(
    "equal empty",
    Array.equal<Int>([], [], Int.equal),
    M.equals(T.bool(true))
  ),
  Suite.test(
    "not equal one empty",
    Array.equal<Int>([], [2, 3], Int.equal),
    M.equals(T.bool(false))
  ),
  Suite.test(
    "not equal different lengths",
    Array.equal<Int>([1, 2, 3], [2, 4], Int.equal),
    M.equals(T.bool(false))
  ),
  Suite.test(
    "not equal same lengths",
    Array.equal<Int>([1, 2, 3], [1, 2, 4], Int.equal),
    M.equals(T.bool(false))
  ),
  Suite.test(
    "find",
    Array.find<Nat>([1, 9, 4, 8], func x = x == 9),
    M.equals(T.optional(T.natTestable, ?9))
  ),
  Suite.test(
    "find fail",
    Array.find<Nat>([1, 9, 4, 8], func _ = false),
    M.equals(T.optional(T.natTestable, null : ?Nat))
  ),
  Suite.test(
    "find empty",
    Array.find<Nat>([], func _ = true),
    M.equals(T.optional(T.natTestable, null : ?Nat))
  ),
  Suite.test(
    "append",
    Array.append<Int>([1, 2, 3], [4, 5, 6]),
    M.equals(T.array<Int>(T.intTestable, [ 1, 2, 3, 4, 5, 6 ]))
  ),
  Suite.test(
    "append first empty",
    Array.append<Int>([], [4, 5, 6]),
    M.equals(T.array<Int>(T.intTestable, [4, 5, 6]))
  ),
  Suite.test(
    "append second empty",
    Array.append<Int>([1, 2, 3], []),
    M.equals(T.array<Int>(T.intTestable, [1, 2, 3]))
  ),
  Suite.test(
    "append both empty",
    Array.append<Int>([], []),
    M.equals(T.array<Int>(T.intTestable, []))
  ),
  Suite.test("sort",
    Array.sort([2, 3, 1], Nat.compare),
    M.equals(T.array<Nat>(T.natTestable, [1, 2, 3]))
  ),
  Suite.test("sort empty array",
    Array.sort([], Nat.compare),
    M.equals(T.array<Nat>(T.natTestable, []))
  ),
  Suite.test("sort already sorted",
    Array.sort([1, 2, 3, 4, 5], Nat.compare),
    M.equals(T.array<Nat>(T.natTestable, [1, 2, 3, 4, 5]))
  ),
  Suite.test("sort repeated elements",
    Array.sort([2, 2, 2, 2, 2], Nat.compare),
    M.equals(T.array<Nat>(T.natTestable, [2, 2, 2, 2, 2]))
  ),
  Suite.test("sortInPlace",
    do {
      let array = [var 2, 3, 1];
      Array.sortInPlace(array, Nat.compare);
      Array.freeze(array)
    },
    M.equals(T.array<Nat>(T.natTestable, [1, 2, 3]))
  ),
  Suite.test("sortInPlace empty",
    do {
      let array = [var];
      Array.sortInPlace(array, Nat.compare);
      Array.freeze(array)
    },
    M.equals(T.array<Nat>(T.natTestable, []))
  ),
  Suite.test("sortInPlace already sorted",
    do {
      let array = [var 1, 2, 3, 4, 5];
      Array.sortInPlace(array, Nat.compare);
      Array.freeze(array)
    },
    M.equals(T.array<Nat>(T.natTestable, [1, 2, 3, 4, 5]))
  ),
  Suite.test("sortInPlace repeated elements",
    do {
      let array = [var 2, 2, 2, 2, 2];
      Array.sortInPlace(array, Nat.compare);
      Array.freeze(array)
    },
    M.equals(T.array<Nat>(T.natTestable, [2, 2, 2, 2, 2]))
  ),
  Suite.test(
    "reverse",
    Array.reverse<Nat>([0, 1, 2, 2, 3]),
    M.equals(T.array<Nat>(T.natTestable, [3, 2, 2, 1, 0]))
  ),
  Suite.test(
    "reverse empty",
    Array.reverse<Nat>([]),
    M.equals(T.array<Nat>(T.natTestable, []))
  ),
  Suite.test(
    "reverse singleton",
    Array.reverse<Nat>([0]),
    M.equals(T.array<Nat>(T.natTestable, [0]))
  ),
  Suite.test(
    "map",
    Array.map<Nat, Bool>([1, 2, 3], func x = x % 2 == 0),
    M.equals(T.array<Bool>(T.boolTestable, [false, true, false]))
  ),
  Suite.test(
    "map empty",
    Array.map<Nat, Bool>([], func x = x % 2 == 0),
    M.equals(T.array<Bool>(T.boolTestable, []))
  ),
  Suite.test(
    "filter",
    Array.filter<Nat>([1, 2, 3, 4, 5, 6], func x = x % 2 == 0),
    M.equals(T.array<Nat>(T.natTestable, [2, 4, 6]))
  ),
  Suite.test(
    "filter empty",
    Array.filter<Nat>([], func x = x % 2 == 0),
    M.equals(T.array<Nat>(T.natTestable, []))
  ),
  Suite.test(
    "mapEntries",
    Array.mapEntries<Nat, Nat>([1, 2, 3], func (x, i) = x + i),
    M.equals(T.array<Nat>(T.natTestable, [1, 3, 5]))
  ),
  Suite.test(
    "mapEntries empty",
    Array.mapEntries<Nat, Nat>([], func (x, i) = x + i),
    M.equals(T.array<Nat>(T.natTestable, []))
  ),
  Suite.test(
    "mapFilter",
    Array.mapFilter<Nat, Nat>([1, 2, 3, 4, 5, 6], func x { if (x % 2 == 0) ?x else null }),
    M.equals(T.array<Nat>(T.natTestable, [2, 4, 6]))
  ),
  Suite.test(
    "mapFilter keep all",
    Array.mapFilter<Nat, Nat>([1, 2, 3], func x = ?x),
    M.equals(T.array<Nat>(T.natTestable, [1, 2, 3]))
  ),
  Suite.test(
    "mapFilter keep none",
    Array.mapFilter<Nat, Nat>([1, 2, 3], func _ = null),
    M.equals(T.array<Nat>(T.natTestable, []))
  ),
  Suite.test(
    "mapFilter empty",
    Array.mapFilter<Nat, Nat>([], func x { if (x % 2 == 0) ?x else null }),
    M.equals(T.array<Nat>(T.natTestable, []))
  ),
  Suite.test(
    "mapResult",
    Array.mapResult<Int, Nat, Text>(
      [1, 2, 3],
      func x { if (x >= 0) { #ok(Int.abs x) } else { #err "error message"} }),
    M.equals(T.result<[Nat], Text>(T.arrayTestable(T.natTestable), T.textTestable, #ok([1, 2, 3])))
  ),
  Suite.test(
    "mapResult fail first",
    Array.mapResult<Int, Nat, Text>(
      [-1, 2, 3],
      func x { if (x >= 0) { #ok(Int.abs x) } else { #err "error message"} }),
    M.equals(T.result<[Nat], Text>(T.arrayTestable(T.natTestable), T.textTestable, #err "error message"))
  ),
  Suite.test(
    "mapResult fail last",
    Array.mapResult<Int, Nat, Text>(
      [1, 2, -3],
      func x { if (x >= 0) { #ok(Int.abs x) } else { #err "error message"} }),
    M.equals(T.result<[Nat], Text>(T.arrayTestable(T.natTestable), T.textTestable, #err "error message"))
  ),
  Suite.test(
    "mapResult empty",
    Array.mapResult<Nat, Nat, Text>(
      [],
      func x = #ok x),
    M.equals(T.result<[Nat], Text>(T.arrayTestable(T.natTestable), T.textTestable, #ok([])))
  ),
  Suite.test(
    "chain",
    Array.chain<Int, Int>([0, 1, 2], func x = [x, -x]),
    M.equals(T.array<Int>(T.intTestable, [0, 0, 1, -1, 2, -2]))
  ),
  Suite.test(
    "chain empty",
    Array.chain<Int, Int>([], func x = [x, -x]),
    M.equals(T.array<Int>(T.intTestable, []))
  ),
  Suite.test(
    "foldLeft",
    Array.foldLeft<Text, Text>([ "a", "b", "c" ], "", Text.concat),
    M.equals(T.text("abc"))
  ),
  Suite.test(
    "foldLeft empty",
    Array.foldLeft<Text, Text>([], "base", Text.concat),
    M.equals(T.text("base"))
  ),
  Suite.test(
    "foldRight",
    Array.foldRight<Text, Text>([ "a", "b", "c" ], "", func (x, acc) = acc # x),
    M.equals(T.text("cba"))
  ),
  Suite.test(
    "foldRight empty",
    Array.foldRight<Text, Text>([], "base", Text.concat),
    M.equals(T.text("base"))
  ),
  Suite.test(
    "flatten",
    Array.flatten<Int>([[1, 2, 3], [], [1]]),
    M.equals(T.array<Int>(T.intTestable, [1, 2, 3, 1]))
  ),
  Suite.test(
    "flatten empty start",
    Array.flatten<Int>([[], [1, 2, 3], [], [1]]),
    M.equals(T.array<Int>(T.intTestable, [1, 2, 3, 1]))
  ),
  Suite.test(
    "flatten empty end",
    Array.flatten<Int>([[1, 2, 3], [], [1], []]),
    M.equals(T.array<Int>(T.intTestable, [1, 2, 3, 1]))
  ),
  Suite.test(
    "flatten singleton",
    Array.flatten<Int>([[1, 2, 3]]),
    M.equals(T.array<Int>(T.intTestable, [1, 2, 3]))
  ),
  Suite.test(
    "flatten empty",
    Array.flatten<Int>([[]]),
    M.equals(T.array<Int>(T.intTestable, []))
  ),
  Suite.test(
    "flatten empty",
    Array.flatten<Int>([]),
    M.equals(T.array<Int>(T.intTestable, []))
  ),
  Suite.test(
    "make",
    Array.make<Int>(0),
    M.equals(T.array<Int>(T.intTestable, [0]))
  ),
  Suite.test(
    "vals",
    do {
      var sum = 0;
      for (x in Array.vals([1, 2, 3])) {
        sum += x;
      };
      sum
    },
    M.equals(T.nat(6))
  ),
  Suite.test(
    "vals empty",
    do {
      var sum = 0;
      for (x in Array.vals([])) {
        sum += x;
      };
      sum
    },
    M.equals(T.nat(0))
  ),
  Suite.test(
    "keys",
    do {
      var sum = 0;
      for (x in Array.keys([1, 2, 3])) {
        sum += x;
      };
      sum
    },
    M.equals(T.nat(3))
  ),
  Suite.test(
    "keys empty",
    do {
      var sum = 0;
      for (x in Array.keys([])) {
        sum += x;
      };
      sum
    },
    M.equals(T.nat(0))
  ),
]);

Suite.run(suite);

funcTest.mo.content:
import Function "mo:base/Func";
import Debug "mo:base/Debug";
import Text "mo:base/Text";

Debug.print("Function");

do {
  Debug.print("  compose");

  func isEven(x : Int) : Bool { x % 2 == 0; };
  func not_(x : Bool) : Bool { not x; };
  let isOdd = Function.compose<Int, Bool, Bool>(not_, isEven);

  assert(isOdd(0) == false);
  assert(isOdd(1));
};

do {
  Debug.print("  const");

  assert(Function.const<Bool, Text>(true)("abc"));
  assert(Function.const<Bool, Text>(false)("abc") == false);
  assert(Function.const<Bool, (Text, Text)>(false)("abc", "abc") == false);
};

TrieExampleTest.mo.content:
import Trie "mo:base/Trie";
import Text "mo:base/Text";
import Iter "mo:base/Iter";

debug {
  type Trie<K, V> = Trie.Trie<K, V>;
  type Key<K> = Trie.Key<K>;

  func key(t: Text) : Key<Text> { { key = t; hash = Text.hash t } };

  let t0 : Trie<Text, Nat> = Trie.empty();
  let t1 : Trie<Text, Nat> = Trie.put(t0, key "hello", Text.equal, 42).0;
  let t2 : Trie<Text, Nat> = Trie.put(t1, key "world", Text.equal, 24).0;
  let n : ?Nat = Trie.put(t1, key "hello", Text.equal, 0).1;
  assert (n == ?42);

  /// True if elements of a form a subset of those of b.
  func isSubSet<X>(a : [X], b : [X], eq : (X, X) -> Bool) : Bool {
    for (x in a.vals()) {
      var found = false;
      label here : () {
        for (y in b.vals()) {
          if (eq(x, y)) { found := true; break here };
        }
      };
      if (not found) { return false };
    };
    return true
  };

  // note that `put("hello", ..., 0)` happens "after" t2, but map is immutable (applicative).
  let actual : [(Text, Nat)] = Iter.toArray(Trie.iter(t2));
  let expected : [(Text, Nat)] = [("hello", 42), ("world", 24)];
  func equalKV(a : (Text, Nat), b : (Text, Nat)) : Bool { a == b };
  assert (isSubSet(actual, expected, equalKV));
  assert (isSubSet(expected, actual, equalKV));
  assert Trie.isValid(t2, false);
};

dequeTest.mo.content:
import Deque "mo:base/Deque";
import Iter "mo:base/Iter";
import O "mo:base/Option";
import D "mo:base/Debug";

// test for Queue
do {
    var l = Deque.empty<Nat>();
    for (i in Iter.range(0, 100)) {
        l := Deque.pushBack(l, i);
    };
    for (i in Iter.range(0, 100)) {
        let x = Deque.peekFront(l);
        switch (Deque.popFront(l)) {
          case (?(y, l2)) { l := l2;
             switch x {
               case null assert false;
               case (?x) assert (x == y) } };
          case null { assert false };
        };
        assert(O.unwrap(x) == i);
    };
    O.assertNull(Deque.peekFront<Nat>(l));
};

// test for Deque
do {
    var l = Deque.empty<Int>();
    for (i in Iter.range(1, 100)) {
        l := Deque.pushFront(l, -i);
        l := Deque.pushBack(l, i);
    };
    label F for (i in Iter.revRange(100, -100)) {
        if (i == 0) continue F;
        let x = Deque.peekBack(l);
        switch (Deque.popBack(l)) {
          case (?(l2, y)) { l := l2;
             switch x {
               case null assert false;
               case (?x) assert (x == y) } };
          case null { assert false };
        };
        assert(O.unwrap(x) == i);
    };
};

optionTest.mo.content:
import Option "mo:base/Option";
import Debug "mo:base/Debug";

Debug.print("Option");

do {
  Debug.print("  apply");

  do {
    Debug.print("    null function, null value");

    let actual = Option.apply<Int, Bool>(null, null);
    let expected : ?Bool = null;

    switch (actual, expected) {
      case (?actual_, ?expected_) {
        assert(false);
      };
      case (_, _) {
        assert(true);
      };
    };
  };

  do {
    Debug.print("    null function, non-null value");

     let actual = Option.apply<Int, Bool>(?0, null);
    let expected : ?Bool = null;

     switch (actual, expected) {
      case (?actual_, ?expected_) {
        assert(false);
      };
      case (_, _) {
        assert(true);
      };
    };
  };

  do {
    Debug.print("    non-null function, null value");

     let isEven = func (x : Int) : Bool {
      x % 2 == 0;
    };

    let actual = Option.apply<Int, Bool>(null, ?isEven);
    let expected : ?Bool = null;

    switch (actual, expected) {
      case (?actual_, ?expected_) {
        assert(false);
      };
      case (_, _) {
        assert(true);
      };
    };
  };

  do {
   Debug.print("    non-null function, non-null value");

   let isEven = func (x : Int) : Bool {
      x % 2 == 0;
    };

    let actual = Option.apply<Int, Bool>(?0, ?isEven);
    let expected = ?true;

    switch (actual, expected) {
      case (?actual_, ?expected_) {
        assert(actual_ == expected_);
      };
      case (_, _) {
        assert(false);
      };
    };
  };

 };

do {
  Debug.print("  bind");

  do {
    Debug.print("    null value to null value");

    let safeInt = func (x : Int) : ?Int {
      if (x > 9007199254740991) {
        null;
      } else {
        ?x;
      }
    };

    let actual = Option.chain<Int, Int>(null, safeInt);
    let expected : ?Int = null;

    switch (actual, expected) {
      case (?actual_, ?expected_) {
        assert(false);
      };
      case (_, _) {
        assert(true);
      };
    };
  };

  do {
    Debug.print("    non-null value to null value");

