heapTest.mo.content:
import H "mo:base/Heap";
import I "mo:base/Iter";
import O "mo:base/Option";
import Int "mo:base/Int";

let order = Int.compare;

do {
    var pq = H.Heap<Int>(order);
    for (i in I.revRange(100, 0)) {
        pq.put(i);
        let x = pq.peekMin();
        assert(O.unwrap(x) == i);
    };
    for (i in I.range(0, 100)) {
        pq.put(i);
        let x = pq.peekMin();
        assert(O.unwrap(x) == 0);
    };
    for (i in I.range(0, 100)) {
        pq.deleteMin();
        let x = pq.peekMin();
        pq.deleteMin();
        assert(O.unwrap(x) == i);
    };
    O.assertNull(pq.peekMin());
};

// fromIter
do {
    do {
        let iter = [5,10,9,7,3,8,1,0,2,4,6].vals();
        let pq = H.fromIter<Int>(iter, order);
        for (i in I.range(0, 10)) {
            let x = pq.peekMin();
            assert(O.unwrap(x) == i);
            pq.deleteMin();
        };
        O.assertNull(pq.peekMin());
    };

    do {
        let pq = H.fromIter<Int>([].vals(), order);
        O.assertNull(pq.peekMin());
    };

    do {
        let pq = H.fromIter<Int>([100].vals(), order);
        assert(O.unwrap(pq.peekMin()) == 100);
    };
};

listTest.mo.content:
import List "mo:base/List";
import Debug "mo:base/Debug";
import Int "mo:base/Int";
import Iter "mo:base/Iter";
import Result "mo:base/Result";
import Suite "mo:matchers/Suite";
import M "mo:matchers/Matchers";
import T "mo:matchers/Testable";


type X = Nat;

  func opnatEq(a : ?Nat, b : ?Nat) : Bool {
    switch (a, b) {
    case (null, null) { true };
    case (?aaa, ?bbb) { aaa == bbb };
    case (_,    _   ) { false };
    }
  };
  func opnat_isnull(a : ?Nat) : Bool {
    switch a {
    case (null) { true };
    case (?aaa) { false };
    }
  };

  // ## Construction
  let l1 = List.nil<X>();
  let l2 = List.push<X>(2, l1);
  let l3 = List.push<X>(3, l2);

  // ## Projection -- use nth
  assert (opnatEq(List.get<X>(l3, 0), ?3));
  assert (opnatEq(List.get<X>(l3, 1), ?2));
  assert (opnatEq(List.get<X>(l3, 2), null));
  //assert (opnatEq (hd<X>(l3), ?3));
  //assert (opnatEq (hd<X>(l2), ?2));
  //assert (opnat_isnull(hd<X>(l1)));

  /*
   // ## Projection -- use nth
   assert (opnatEq(nth<X>(l3, 0), ?3));
   assert (opnatEq(nth<X>(l3, 1), ?2));
   assert (opnatEq(nth<X>(l3, 2), null));
   assert (opnatEq (hd<X>(l3), ?3));
   assert (opnatEq (hd<X>(l2), ?2));
   assert (opnat_isnull(hd<X>(l1)));
   */

  // ## Deconstruction
  let (a1, t1) = List.pop<X>(l3);
  assert (opnatEq(a1, ?3));
  let (a2, t2) = List.pop<X>(l2);
  assert (opnatEq(a2, ?2));
  let (a3, t3) = List.pop<X>(l1);
  assert (opnatEq(a3, null));
  assert (List.isNil<X>(t3));

  // ## List functions
  assert (List.size<X>(l1) == 0);
  assert (List.size<X>(l2) == 1);
  assert (List.size<X>(l3) == 2);

  // ## List functions
  assert (List.size<X>(l1) == 0);
  assert (List.size<X>(l2) == 1);
  assert (List.size<X>(l3) == 2);

  do {
    Debug.print("  flatten");

    let expected : List.List<Nat> = ?(1, ?(2, ?(3, null)));
    // [[1, 2], [3]]
    let nested : List.List<List.List<Nat>> =
      ?(?(1, ?(2, null)), ?(?(3, null), null));
    let actual = List.flatten<Nat>(nested);

    assert List.equal<Nat>(expected, actual, func (x1, x2) { x1 == x2 });

  };

  do {
    Debug.print("  fromArray");

    let expected : List.List<Nat> = ?(1, ?(2, ?(3, List.nil<Nat>())));
    let array = [1, 2, 3];
    let actual = List.fromArray<Nat>(array);

    assert List.equal<Nat>(expected, actual, func (x1, x2) { x1 == x2 });
  };

  do {
    Debug.print("  fromVarArray");

    let expected : List.List<Nat> = ?(1, ?(2, ?(3, List.nil<Nat>())));
    let array = [var 1, 2, 3];
    let actual = List.fromVarArray<Nat>(array);

    assert List.equal<Nat>(expected, actual, func (x1, x2) { x1 == x2 });
  };

  do {
    Debug.print("  toArray");

    let expected = [1, 2, 3];
    let list : List.List<Nat> = ?(1, ?(2, ?(3, List.nil<Nat>())));
    let actual = List.toArray<Nat>(list);

    assert (actual.size() == expected.size());

    for (i in actual.keys()) {
      assert(actual[i] == expected[i]);
    };
  };

  do {
    Debug.print("  toVarArray");

    let expected = [var 1, 2, 3];
    let list : List.List<Nat> = ?(1, ?(2, ?(3, List.nil<Nat>())));
    let actual = List.toVarArray<Nat>(list);

    assert (actual.size() == expected.size());

    for (i in actual.keys()) {
      assert(actual[i] == expected[i]);
    };
  };

  do {
    Debug.print("  toIter");

    let list : List.List<Nat> = ?(1, ?(2, ?(3, List.nil<Nat>())));
    let _actual = List.toIter<Nat>(list);
    let actual = [var 0, 0, 0];
    let expected = [1, 2, 3];

    Iter.iterate<Nat>(_actual, func (x, i) { actual[i] := x; });

    for (i in actual.keys()) {
      assert(actual[i] == expected[i]);
    };
  };

func makeNatural(x : Int) : Result.Result<Nat, Text> =
  if (x >= 0) { #ok(Int.abs(x)) } else { #err(Int.toText(x) # " is not a natural number.") };

func listRes(itm : Result.Result<List.List<Nat>, Text>) : T.TestableItem<Result.Result<List.List<Nat>, Text>> {
  let resT = T.resultTestable(T.listTestable<Nat>(T.intTestable), T.textTestable);
  { display = resT.display; equals = resT.equals; item = itm }
};

let mapResult = Suite.suite("mapResult", [
  Suite.test("empty list",
    List.mapResult<Int, Nat, Text>(List.nil(), makeNatural),
    M.equals(listRes(#ok(List.nil())))
  ),
  Suite.test("success",
    List.mapResult<Int, Nat, Text>(?(1, ?(2, ?(3, null))), makeNatural),
    M.equals(listRes(#ok(?(1, ?(2, ?(3, null))))))
  ),
  Suite.test("fail fast",
    List.mapResult<Int, Nat, Text>(?(-1, ?(2, ?(3, null))), makeNatural),
    M.equals(listRes(#err("-1 is not a natural number.")))
  ),
  Suite.test("fail last",
    List.mapResult<Int, Nat, Text>(?(1, ?(2, ?(-3, null))), makeNatural),
    M.equals(listRes(#err("-3 is not a natural number.")))
  ),
]);

Suite.run(Suite.suite("List", [ mapResult ]));

let replicate = Suite.suite("replicate", [
  Suite.test("empty-list",
    List.replicate<Nat>(0, 0),
    M.equals(
      T.list(T.natTestable, List.nil<Nat>()))
  ),
  Suite.test("small-list",
    List.replicate(3, 0),
    M.equals(
      T.list<Nat>(T.natTestable, ?(0, ?(0, ?(0, null)))))
  )
]);

let tabulate = Suite.suite("tabulate", [
  Suite.test("empty-list",
    List.tabulate<Nat>(0, func i { i }),
    M.equals(
      T.list(T.natTestable, List.nil<Nat>()))
  ),
  Suite.test("small-list",
    List.tabulate<Nat>(3, func i { i * 2 }),
    M.equals(
      T.list<Nat>(T.natTestable, ?(0, ?(2, ?(4, null)))))
  ),
  Suite.test("large-list",
    List.tabulate<Nat>(10000, func i { 0 }),
    M.equals(
      T.list<Nat>(T.natTestable, List.replicate(10000, 0)))
  )
]);

let append = Suite.suite("append", [
  Suite.test("small-list",
    List.append(
      List.tabulate<Nat>(10, func i { i }),
      List.tabulate<Nat>(10, func i { i + 10 })),
    M.equals(
      T.list(T.natTestable, List.tabulate<Nat>(20, func i { i })))
  ),
  Suite.test("large-list",
    List.append(
      List.tabulate<Nat>(10000, func i { i }),
      List.tabulate<Nat>(10000, func i { i + 10000 })),
    M.equals(
      T.list(T.natTestable,List.tabulate<Nat>(20000, func i { i })))
  ),
]);

Suite.run(Suite.suite("List", [ mapResult, replicate, tabulate, append ]));

iterTest.mo.content:
import Iter "mo:base/Iter";
import Array "mo:base/Array";
import List "mo:base/List";
import Nat "mo:base/Nat";
import Int "mo:base/Int";
import Debug "mo:base/Debug";

Debug.print("Iter");

do {
  Debug.print("  range");

  let tests = [((0,-1), "", "0-1"), ((0,0), "0", "0"), ((0, 5), "012345", ""), ((5, 0), "", "543210")];
  for ((range, expected, revExpected) in tests.vals()) {
      var x = "";
      for (i in Iter.range(range)) {
          x := x # Nat.toText(i);
      };
      assert(x == expected);
      x := "";
      for (i in Iter.revRange(range)) {
          x := x # Int.toText(i);
      };
      assert(x == revExpected);
  };
};

do {
  Debug.print("  iterate");

  let xs = [ "a", "b", "c", "d", "e", "f" ];

  var y = "";
  var z = 0;

  Iter.iterate<Text>(xs.vals(), func (x : Text, i : Nat) {
    y := y # x;
    z += i;
  });

  assert(y == "abcdef");
  assert(z == 15);
};

do {
  Debug.print("  map");

  let isEven = func (x : Int) : Bool {
    x % 2 == 0;
  };

  let _actual = Iter.map<Nat, Bool>([ 1, 2, 3 ].vals(), isEven);
  let actual = [var true, false, true];
  Iter.iterate<Bool>(_actual, func (x, i) { actual[i] := x; });

  let expected = [false, true, false];

  for (i in actual.keys()) {
    assert(actual[i] == expected[i]);
  };
};

do {
  Debug.print("  filter");

  let isOdd = func (x : Int) : Bool {
    x % 2 == 1;
  };

  let _actual = Iter.filter<Nat>([ 1, 2, 3 ].vals(), isOdd);
  let actual = [var 0, 0];
  Iter.iterate<Nat>(_actual, func (x, i) { actual[i] := x; });

  let expected = [1, 3];

  assert(Array.freeze(actual) == expected);
};

do {
  Debug.print("  make");

  let x = 1;
  let y = Iter.make<Nat>(x);

  switch (y.next()) {
    case null { assert false; };
    case (?z) { assert (x == z); };
  };
};

do {
  Debug.print("  fromArray");

  let expected = [1, 2, 3];
  let _actual = Iter.fromArray<Nat>(expected);
  let actual = [var 0, 0, 0];

  Iter.iterate<Nat>(_actual, func (x, i) { actual[i] := x; });

  for (i in actual.keys()) {
    assert(actual[i] == expected[i]);
  };
};

do {
  Debug.print("  fromArrayMut");

  let expected = [var 1, 2, 3];
  let _actual = Iter.fromArrayMut<Nat>(expected);
  let actual = [var 0, 0, 0];

  Iter.iterate<Nat>(_actual, func (x, i) { actual[i] := x; });

  for (i in actual.keys()) {
    assert(actual[i] == expected[i]);
  };
};

do {
  Debug.print("  fromList");

  let list : List.List<Nat> = ?(1, ?(2, ?(3, List.nil<Nat>())));
  let _actual = Iter.fromList<Nat>(list);
  let actual = [var 0, 0, 0];
  let expected = [1, 2, 3];

  Iter.iterate<Nat>(_actual, func (x, i) { actual[i] := x; });

  for (i in actual.keys()) {
    assert(actual[i] == expected[i]);
  };
};

do {
  Debug.print("  toArray");

  let expected = [1, 2, 3];
  let actual = Iter.toArray<Nat>(expected.vals());

  assert (actual.size() == expected.size());

  for (i in actual.keys()) {
    assert(actual[i] == expected[i]);
  };
};

do {
  Debug.print("  toArrayMut");

  let expected = [var 1, 2, 3];
  let actual = Iter.toArrayMut<Nat>(expected.vals());

  assert (actual.size() == expected.size());

  for (i in actual.keys()) {
    assert(actual[i] == expected[i]);
  };
};

do {
  Debug.print("  toList");

  let expected : List.List<Nat> = ?(1, ?(2, ?(3, List.nil<Nat>())));
  let actual = Iter.toList<Nat>([1, 2, 3].vals());
  assert List.equal<Nat>(expected, actual, func (x1, x2) { x1 == x2 });
};


do {
  Debug.print("  sort");

  let input : [Nat] = [4, 3, 1, 2, 5];
  let expected : [Nat] = [1, 2, 3, 4, 5];
  let actual = Iter.toArray(Iter.sort<Nat>(input.vals(), Nat.compare));
  assert Array.equal<Nat>(expected, actual, func (x1, x2) { x1 == x2 });
};

stackTest.mo.content:
import Stack "mo:base/Stack";
import Iter "mo:base/Iter";
import O "mo:base/Option";

do {
    var s = Stack.Stack<Nat>();
    for (i in Iter.range(0, 100)) {
        s.push(i);
    };
    for (i in Iter.revRange(100, 0)) {
        let x = s.pop();
        assert(O.unwrap(x) == i);
    };
    assert (s.isEmpty());
};

