import Prim "mo:â›”";
import H "mo:base/HashMap";
import Hash "mo:base/Hash";
import Text "mo:base/Text";

debug {
  let a = H.HashMap<Text, Nat>(3, Text.equal, Text.hash);

  a.put("apple", 1);
  a.put("banana", 2);
  a.put("pear", 3);
  a.put("avocado", 4);
  a.put("Apple", 11);
  a.put("Banana", 22);
  a.put("Pear", 33);
  a.put("Avocado", 44);
  a.put("ApplE", 111);
  a.put("BananA", 222);
  a.put("PeaR", 333);
  a.put("AvocadO", 444);

  // need to resupply the constructor args; they are private to the object; but, should they be?
  let b = H.clone<Text, Nat>(a, Text.equal, Text.hash);

  // ensure clone has each key-value pair present in original
  for ((k,v) in a.entries()) {
    Prim.debugPrint(debug_show (k,v));
    switch (b.get(k)) {
    case null { assert false };
    case (?w) { assert v == w };
    };
  };

  // ensure original has each key-value pair present in clone
  for ((k,v) in b.entries()) {
    Prim.debugPrint(debug_show (k,v));
    switch (a.get(k)) {
    case null { assert false };
    case (?w) { assert v == w };
    };
  };

  // ensure clone has each key present in original
  for (k in a.keys()) {
    switch (b.get(k)) {
    case null { assert false };
    case (?_) {  };
    };
  };

  // ensure clone has each value present in original
  for (v in a.vals()) {
    var foundMatch = false;
    for (w in b.vals()) {
      if (v == w) { foundMatch := true }
    };
    assert foundMatch
  };

  // do some more operations:
  a.put("apple", 1111);
  a.put("banana", 2222);
  switch( a.remove("pear")) {
    case null { assert false };
    case (?three) { assert three == 3 };
  };
  a.delete("avocado");

  // check them:
  switch (a.get("apple")) {
  case (?1111) { };
  case _ { assert false };
  };
  switch (a.get("banana")) {
  case (?2222) { };
  case _ { assert false };
  };
  switch (a.get("pear")) {
  case null {  };
  case (?_) { assert false };
  };
  switch (a.get("avocado")) {
  case null {  };
  case (?_) { assert false };
  };

  // undo operations above:
  a.put("apple", 1);
  // .. and test that replace works
  switch (a.replace("apple", 666)) {
    case null { assert false };
    case (?one) { assert one == 1; // ...and revert
                  a.put("apple", 1)
         };
  };
  a.put("banana", 2);
  a.put("pear", 3);
  a.put("avocado", 4);

  // ensure clone has each key-value pair present in original
  for ((k,v) in a.entries()) {
    Prim.debugPrint(debug_show (k,v));
    switch (b.get(k)) {
    case null { assert false };
    case (?w) { assert v == w };
    };
  };

  // ensure original has each key-value pair present in clone
  for ((k,v) in b.entries()) {
    Prim.debugPrint(debug_show (k,v));
    switch (a.get(k)) {
    case null { assert false };
    case (?w) { assert v == w };
    };
  };


  // test fromIter method
  let c = H.fromIter<Text, Nat>(b.entries(), 0, Text.equal, Text.hash);

  // c agrees with each entry of b
  for ((k,v) in b.entries()) {
    Prim.debugPrint(debug_show (k,v));
    switch (c.get(k)) {
    case null { assert false };
    case (?w) { assert v == w };
    };
  };

  // b agrees with each entry of c
  for ((k,v) in c.entries()) {
    Prim.debugPrint(debug_show (k,v));
    switch (b.get(k)) {
    case null { assert false };
    case (?w) { assert v == w };
    };
  };

  // Issue #228
  let d = H.HashMap<Text, Nat>(50, Text.equal, Text.hash);
  switch(d.remove("test")) {
    case null { };
    case (?_) { assert false };
  };
};

bufTest.mo.content:
import Prim "mo:â›”";
import B "mo:base/Buffer";
import Iter "mo:base/Iter";
import Option "mo:base/Option";
import Nat "mo:base/Nat";
import Hash "mo:base/Hash";
import Nat32 "mo:base/Nat32";
import Order "mo:base/Order";

import Suite "mo:matchers/Suite";
import T "mo:matchers/Testable";
import M "mo:matchers/Matchers";

let {run;test;suite} = Suite;

let NatBufferTestable : T.Testable<B.Buffer<Nat>> = object {
  public func display(buffer : B.Buffer<Nat>) : Text {
    B.toText(buffer, Nat.toText);
  };
  public func equals(buffer1 : B.Buffer<Nat>, buffer2 : B.Buffer<Nat>) : Bool {
    B.equal(buffer1, buffer2, Nat.equal)
  };
};

class OrderTestable(initItem : Order.Order) : T.TestableItem<Order.Order> {
  public let item = initItem;
  public func display(order : Order.Order) : Text {
    switch (order) {
      case (#less) {
        "#less"
      };
      case (#greater) {
        "#greater"
      };
      case (#equal) {
        "#equal"
      }
    }
  };
  public let equals = Order.equal;
};

/* --------------------------------------- */
run(suite("construct",
[
  test(
    "initial size",
    B.Buffer<Nat>(10).size(),
    M.equals(T.nat(0))
  ),
  test(
    "initial capacity",
    B.Buffer<Nat>(10).capacity(),
    M.equals(T.nat(10))
  ),
]));

/* --------------------------------------- */

var buffer = B.Buffer<Nat>(10);
for (i in Iter.range(0, 3)) {
  buffer.add(i);
};

run(suite("add",
[
  test(
    "size",
    buffer.size(),
    M.equals(T.nat(4))
  ),
  test(
    "capacity",
    buffer.capacity(),
    M.equals(T.nat(10))
  ),
  test(
    "elements",
    B.toArray(buffer),
    M.equals(T.array<Nat>(T.natTestable, [0, 1, 2, 3]))
  ),
]));

/* --------------------------------------- */
buffer := B.Buffer<Nat>(2);
for (i in Iter.range(0, 3)) {
  buffer.add(i);
};

run(suite("add with capacity change",
[
  test(
    "size",
    buffer.size(),
    M.equals(T.nat(4))
  ),
  test(
    "capacity",
    buffer.capacity(),
    M.equals(T.nat(5))
  ),
  test(
    "elements",
    B.toArray(buffer),
    M.equals(T.array<Nat>(T.natTestable, [0, 1, 2, 3]))
  ),
]));

/* --------------------------------------- */
buffer := B.Buffer<Nat>(0);
for (i in Iter.range(0, 3)) {
  buffer.add(i);
};

run(suite("add with capacity change, initial capacity 0",
[
  test(
    "size",
    buffer.size(),
    M.equals(T.nat(4))
  ),
  test(
    "capacity",
    buffer.capacity(),
    M.equals(T.nat(5))
  ),
  test(
    "elements",
    B.toArray(buffer),
    M.equals(T.array<Nat>(T.natTestable, [0, 1, 2, 3]))
  ),
]));

/* --------------------------------------- */
buffer := B.Buffer<Nat>(2);

run(suite("removeLast on empty buffer",
[
  test(
    "return value",
    buffer.removeLast(),
    M.equals(T.optional(T.natTestable, null : ?Nat))
  ),
  test(
    "size",
    buffer.size(),
    M.equals(T.nat(0))
  ),
  test(
    "capacity",
    buffer.capacity(),
    M.equals(T.nat(2))
  ),
  test(
    "elements",
    B.toArray(buffer),
    M.equals(T.array<Nat>(T.natTestable, []))
  ),
]));

/* --------------------------------------- */
buffer := B.Buffer<Nat>(2);
for (i in Iter.range(0, 5)) {
  buffer.add(i);
};

run(suite("removeLast",
[
  test(
    "return value",
    buffer.removeLast(),
    M.equals(T.optional<Nat>(T.natTestable, ?5))
  ),
  test(
    "size",
    buffer.size(),
    M.equals(T.nat(5))
  ),
  test(
    "capacity",
    buffer.capacity(),
    M.equals(T.nat(8))
  ),
  test(
    "elements",
    B.toArray(buffer),
    M.equals(T.array<Nat>(T.natTestable, [0, 1, 2, 3, 4]))
  ),
]));

/* --------------------------------------- */
buffer := B.Buffer<Nat>(3);
for (i in Iter.range(0, 5)) {
  buffer.add(i);
};

for (i in Iter.range(0, 5)) {
  ignore buffer.removeLast();
};

run(suite("removeLast until empty",
[
  test(
    "return value",
    buffer.removeLast(),
    M.equals(T.optional(T.natTestable, null : ?Nat))
  ),
  test(
    "size",
    buffer.size(),
    M.equals(T.nat(0))
  ),
  test(
    "capacity",
    buffer.capacity(),
    M.equals(T.nat(2))
  ),
  test(
    "elements",
    B.toArray(buffer),
    M.equals(T.array<Nat>(T.natTestable, []))
  ),
]));

/* --------------------------------------- */
buffer := B.Buffer<Nat>(0);
for (i in Iter.range(0, 5)) {
  buffer.add(i);
};

run(suite("remove",
[
  test(
    "return value",
    buffer.remove(2),
    M.equals(T.nat(2))
  ),
  test(
    "size",
    buffer.size(),
    M.equals(T.nat(5))
  ),
  test(
    "capacity",
    buffer.capacity(),
    M.equals(T.nat(8))
  ),
  test(
    "elements",
    B.toArray(buffer),
    M.equals(T.array<Nat>(T.natTestable, [0, 1, 3, 4, 5]))
  ),
  test(
    "return value",
    buffer.remove(0),
    M.equals(T.nat(0))
  ),
  test(
    "size",
    buffer.size(),
    M.equals(T.nat(4))
  ),
  test(
    "capacity",
    buffer.capacity(),
    M.equals(T.nat(8))
  ),
  test(
    "elements",
    B.toArray(buffer),
    M.equals(T.array<Nat>(T.natTestable, [1, 3, 4, 5]))
  ),
]));

/* --------------------------------------- */
buffer := B.Buffer<Nat>(3);
for (i in Iter.range(0, 2)) {
  buffer.add(i);
};

run(suite("remove last element at capacity",
[
  test(
    "return value",
    buffer.remove(2),
    M.equals(T.nat(2))
  ),
  test(
    "size",
    buffer.size(),
    M.equals(T.nat(2))
  ),
  test(
    "capacity",
    buffer.capacity(),
    M.equals(T.nat(3))
  ),
  test(
    "elements",
    B.toArray(buffer),
    M.equals(T.array<Nat>(T.natTestable, [0, 1]))
  ),
]));

/* --------------------------------------- */
buffer := B.Buffer<Nat>(0);
for (i in Iter.range(0, 5)) {
  buffer.add(i);
};

for (i in Iter.range(0, 5)) {
  ignore buffer.remove(5 - i);
};

run(suite("remove until empty",
[
  test(
    "size",
    buffer.size(),
    M.equals(T.nat(0))
  ),
  test(
    "capacity",
    buffer.capacity(),
    M.equals(T.nat(2))
  ),
  test(
    "elements",
    B.toArray(buffer),
    M.equals(T.array<Nat>(T.natTestable, []))
  ),
]));

/* --------------------------------------- */
buffer := B.Buffer<Nat>(1);
for (i in Iter.range(0, 5)) {
  buffer.add(i);
};

buffer.filterEntries(func(_, x) = x % 2 == 0);

run(suite("filterEntries",
[
  test(
    "size",
    buffer.size(),
    M.equals(T.nat(3))
  ),
  test(
    "capacity",
    buffer.capacity(),
    M.equals(T.nat(8))
  ),
  test(
    "elements",
    B.toArray(buffer),
    M.equals(T.array<Nat>(T.natTestable, [0, 2, 4]))
  ),
]));

/* --------------------------------------- */
buffer := B.Buffer<Nat>(1);
buffer.filterEntries(func(_, x) = x % 2 == 0);

run(suite("filterEntries on empty",
[
  test(
    "size",
    buffer.size(),
    M.equals(T.nat(0))
  ),
  test(
    "capacity",
    buffer.capacity(),
    M.equals(T.nat(1))
  ),
  test(
    "elements",
    B.toArray(buffer),
    M.equals(T.array<Nat>(T.natTestable, []))
  ),
]));

/* --------------------------------------- */
buffer := B.Buffer<Nat>(12);
for (i in Iter.range(0, 5)) {
  buffer.add(i);
};
buffer.filterEntries(func(i, x) = i + x == 2);

run(suite("filterEntries size down",
[
  test(
    "size",
    buffer.size(),
    M.equals(T.nat(1))
  ),
  test(
    "capacity",
    buffer.capacity(),
    M.equals(T.nat(6))
  ),
  test(
    "elements",
    B.toArray(buffer),
    M.equals(T.array<Nat>(T.natTestable, [1]))
  ),
]));

/* --------------------------------------- */
buffer := B.Buffer<Nat>(5);
for (i in Iter.range(0, 4)) {
  buffer.add(i);
};
buffer.filterEntries(func(_, _) = false);

run(suite("filterEntries size down to empty",
[
  test(
    "size",
    buffer.size(),
    M.equals(T.nat(0))
  ),
  test(
    "capacity",
    buffer.capacity(),
    M.equals(T.nat(2))
  ),
  test(
    "elements",
    B.toArray(buffer),
    M.equals(T.array<Nat>(T.natTestable, [] : [Nat]))
  ),
]));

/* --------------------------------------- */
buffer := B.Buffer<Nat>(10);
for (i in Iter.range(0, 5)) {
  buffer.add(i);
};

run(suite("get and getOpt",
[
  test(
    "get",
    buffer.get(2),
    M.equals(T.nat(2))
  ),
  test(
    "getOpt success",
    buffer.getOpt(0),
    M.equals(T.optional(T.natTestable, ?0))
  ),
  test(
    "getOpt out of bounds",
    buffer.getOpt(10),
    M.equals(T.optional(T.natTestable, null : ?Nat))
  ),
]));

/* --------------------------------------- */
buffer := B.Buffer<Nat>(10);
for (i in Iter.range(0, 5)) {
  buffer.add(i);
